var i, item, max = 0,
    anagrams = {};
for (i = 0; i < words.length; i += 1) {
    var key = words[i].split('').sort().join('');
    if (!anagrams.hasOwnProperty(key)) { //check if property exists on current obj only
        anagrams[key] = [];
