var i, j;
for (i = 1; i <= 5; i += 1) {
    s = '';
    for (j = 0; j < i; j += 1)
        s += '*';
    document.write(s + '<br>');
}

function range(i) {
    return i ? range(i - 1).concat(i) : [];
}
range(5) -- > [1, 2, 3, 4, 5]

var s = '';
range(5).forEach(
    function(line) {
        range(line).forEach(
            function() {
                s += '*';
            }
        );
        s += '\n';
    }
);
console.log(s);

console.log(
    range(5).reduce(
        function(a, n) {
            return a + Array(n + 1).join('*') + '\n';
        }, ''
    )
);
