function nim() {
    var x = 0,
        a = arguments,
        l = a.length,
        r = [];
    for (var j = 0; j < l; j++) x ^= a[j];
    for (var j = 0; j < l; j++) r[j] = Math.max(0, a[j] - (x ^ a[j]));
    return r;
}

