    // zipListsWith :: ([a] -> b) -> [[a]] -> [[b]]
    function zipListsWith(f, xss) {
        return (xss.length ? xss[0] : [])
            .map(function(_, i) {
                return f(xss.map(function(xs) {
                    return xs[i];
                }));
            });
