// quickSortÂ :: (Ord a) => [a] -> [a]  
function quickSort(xxs) {
    if (xxs.length) {
        var x = xxs[0],
            xs = xxs.slice(1),
            less = quickSort(xs.filter(function(a) {
                return a <= x;
            })),
            more = quickSort(xs.filter(function(a) {
                return a > x;
            }));
        return less
            .concat([x])
            .concat(more);
    } else return xxs;
}
