(function() {
    function wakeSplit(n, sailors, depth) {
        depth = "u" === (typeof depth)[0] ? sailors : depth;
        var portion = Math.floor(n / sailors),
            remain = n % sailors;
        return 0 >= portion || remain !== (depth ? 1 : 0) ?
            null : depth ? wakeSplit(
                n - portion - remain, sailors, depth - 1
            ) : n;
    }
    // for 5, 6, and 7 sailors
    return [5, 6, 7].map(function(sailors) {
        var nuts = sailors;
        while (!wakeSplit(nuts, sailors)) nuts += 1;
        return nuts;
    });
})();
